import React from 'react';
import { Subscription, SubscriptionCategory } from '../../types/subscription';

interface SummaryProps {
  subscriptions: Subscription[];
}

export const SubscriptionSummary: React.FC<SummaryProps> = ({ subscriptions }) => {
  const calculateTotalMonthly = () => {
    return subscriptions.reduce((total, sub) => {
      if (!sub.isActive) return total;
      const monthlyAmount = sub.billingCycle === 'yearly' 
        ? sub.price / 12 
        : sub.price;
      return total + monthlyAmount;
    }, 0);
  };

  const getCategoryCount = () => {
    const activeSubscriptions = subscriptions.filter(s => s.isActive);
    const categoryCount = activeSubscriptions.reduce((acc, sub) => {
      acc[sub.category] = (acc[sub.category] || 0) + 1;
      return acc;
    }, {} as Record<number, number>);

    return {
      [SubscriptionCategory.ENTERTAINMENT]: categoryCount[SubscriptionCategory.ENTERTAINMENT] || 0,
      [SubscriptionCategory.PRODUCTIVITY]: categoryCount[SubscriptionCategory.PRODUCTIVITY] || 0,
      [SubscriptionCategory.LIFESTYLE]: categoryCount[SubscriptionCategory.LIFESTYLE] || 0,
      [SubscriptionCategory.OTHER]: categoryCount[SubscriptionCategory.OTHER] || 0,
    };
  };

  const getCategoryLabel = (category: SubscriptionCategory): string => {
    return {
      [SubscriptionCategory.ENTERTAINMENT]: '엔터테인먼트',
      [SubscriptionCategory.PRODUCTIVITY]: '생산성',
      [SubscriptionCategory.LIFESTYLE]: '라이프스타일',
      [SubscriptionCategory.OTHER]: '기타'
    }[category];
  };

  const categoryCount = getCategoryCount();

  return (
    <div className="summary-container">
      <h2 className="summary-title">나의 구독 요약</h2>
      <div className="summary-stats">
        <div className="stat-item">
          <span>총 구독 수</span>
          <strong>{subscriptions.filter(s => s.isActive).length}개</strong>
        </div>
        <div className="stat-item category-stat">
          <span>카테고리별 구독</span>
          <div className="category-list">
            {Object.entries(categoryCount).map(([category, count]) => (
              count > 0 && (
                <div key={category} className="category-item">
                  <span className="category-name">{getCategoryLabel(Number(category))}</span>
                  <span className="category-count">{count}개</span>
                </div>
              )
            ))}
          </div>
        </div>
        <div className="stat-item">
          <span>월 총액</span>
          <strong>₩{calculateTotalMonthly().toLocaleString()}</strong>
        </div>
      </div>
    </div>
  );
};
