import React from 'react';
import { Recommendation } from '../../types/analytics';

interface RecommendationsListProps {
  recommendations: Recommendation[];
}

export const RecommendationsList: React.FC<RecommendationsListProps> = ({ recommendations }) => {
  const getRecommendationTypeLabel = (type: string): string => {
    const labels = {
      DUPLICATE_SUBSCRIPTION: '중복 구독',
      UNUSED_SUBSCRIPTION: '미사용 구독',
      BETTER_PLAN: '더 나은 요금제'
    };
    return labels[type] || type;
  };

  return (
    <div className="recommendations-container">
      <h2>구독 최적화 추천</h2>
      <div className="recommendations-list">
        {recommendations.map((recommendation, index) => (
          <div key={index} className="recommendation-item">
            <div className="recommendation-type">
              {getRecommendationTypeLabel(recommendation.type)}
            </div>
            <p className="recommendation-description">
              {recommendation.description}
            </p>
            <div className="potential-savings">
              예상 절감액: ₩{recommendation.potentialSavings.toLocaleString()}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
