import React, { useState, useEffect } from 'react';
import { Subscription } from '../../types/subscription';
import { SpendingAnalysis } from '../../types/analytics';
import { SubscriptionSummary } from './SubscriptionSummary';
import { SubscriptionList } from './SubscriptionList';
import { RecommendationsList } from './RecommendationsList';
import { mockSubscriptions, mockAnalysis } from '../../data/mockData';

interface DashboardProps {
  userId: string;
}

export const SubscriptionDashboard: React.FC<DashboardProps> = ({ userId }) => {
  const [subscriptions, setSubscriptions] = useState<Subscription[]>([]);
  const [analysis, setAnalysis] = useState<SpendingAnalysis | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchSubscriptionData();
  }, [userId]);

  const fetchSubscriptionData = async () => {
    try {
      setLoading(true);
      // API 호출을 시뮬레이션
      await new Promise(resolve => setTimeout(resolve, 1000));
      setSubscriptions(mockSubscriptions);
      setAnalysis(mockAnalysis);
    } catch (error) {
      console.error('구독 정보를 불러오는데 실패했습니다:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubscriptionAdd = (newSubscription: Omit<Subscription, 'id'>) => {
    const subscription: Subscription = {
      ...newSubscription,
      id: `subscription-${Date.now()}` // 임시 ID 생성
    };
    setSubscriptions(prev => [...prev, subscription]);
  };

  return (
    <div className="dashboard-container">
      {loading ? (
        <div>로딩 중...</div>
      ) : (
        <>
          <SubscriptionSummary subscriptions={subscriptions} />
          <SubscriptionList 
            subscriptions={subscriptions} 
            onSubscriptionAdd={handleSubscriptionAdd}
          />
          {analysis && <RecommendationsList recommendations={analysis.recommendations} />}
        </>
      )}
    </div>
  );
};
