import { Subscription, SubscriptionCategory } from '../types/subscription';
import { SpendingAnalysis, RecommendationType } from '../types/analytics';

export const mockSubscriptions: Subscription[] = [
  {
    id: '1',
    name: '넷플릭스',
    price: 17000,
    currency: 'KRW',
    billingCycle: 'monthly',
    nextBillingDate: new Date('2024-04-15'),
    category: SubscriptionCategory.ENTERTAINMENT,
    isActive: true
  },
  {
    id: '2',
    name: '지라(Jira)',
    price: 84000,
    currency: 'KRW',
    billingCycle: 'yearly',
    nextBillingDate: new Date('2024-12-31'),
    category: SubscriptionCategory.PRODUCTIVITY,
    isActive: true
  },
  {
    id: '3',
    name: '멜론',
    price: 10900,
    currency: 'KRW',
    billingCycle: 'monthly',
    nextBillingDate: new Date('2024-04-20'),
    category: SubscriptionCategory.ENTERTAINMENT,
    isActive: true
  }
];

export const mockAnalysis: SpendingAnalysis = {
  totalMonthly: 34900,
  totalYearly: 418800,
  categoryBreakdown: {
    [SubscriptionCategory.ENTERTAINMENT]: 27900,
    [SubscriptionCategory.PRODUCTIVITY]: 7000,
    [SubscriptionCategory.LIFESTYLE]: 0,
    [SubscriptionCategory.OTHER]: 0
  },
  recommendations: [
    {
      type: RecommendationType.DUPLICATE_SUBSCRIPTION,
      description: '넷플릭스와 왓챠를 동시에 구독 중입니다. 하나의 서비스만 사용하는 것을 고려해보세요.',
      potentialSavings: 17000,
      subscriptionIds: ['1', '4']
    },
    {
      type: RecommendationType.BETTER_PLAN,
      description: '연간 결제로 전환하면 월 1,000원을 절약할 수 있습니다.',
      potentialSavings: 12000,
      subscriptionIds: ['3']
    }
  ]
};
